class Solution {
public:
class segmentTreeMax {
    public:
        segmentTreeMax() {}
        segmentTreeMax(vector<int>& arr) {
            this->size = arr.size();
            this->arrTree.resize(arr.size() * 4, 0);
            this->lazy.resize(arr.size() * 4, 0);
            structSegmentTree(arr, 1, 0, arr.size() - 1);
        }

        void updateArea(int L, int R, int val){
            updateAreaTree(1, 0, this->size - 1, L, R, val);
        }

        int findMax(int L, int R){
            return getAreaSum(1, 0, this->size - 1, L, R);
        }

    private:
        void push_down(int index, int l, int r) {
            if (lazy[index]) {
                int mid = l + (r - l) / 2;
                lazy[index * 2] += lazy[index];
                arrTree[index * 2] -= lazy[index];
                lazy[index * 2 + 1] += lazy[index];
                arrTree[index * 2 + 1] -= lazy[index];
                lazy[index] = 0;
            }
        }

        void push_up(int index) {
            arrTree[index] =
                max(arrTree[index * 2], arrTree[index * 2 + 1]);
        }

        void updateAreaTree(int index, int l, int r, int L, int R, int val) { // 
            if (L <= l && r <= R) {
                lazy[index] += val;
                arrTree[index] -= val;
                return;
            }
            push_down(index, l, r);
            int mid = l + (r - l) / 2;
            if (L <= mid) {
                updateAreaTree(index * 2, l, mid, L, R, val);
            }
            if (R > mid) {
                updateAreaTree(index * 2 + 1, mid + 1, r, L, R, val);
            }
            push_up(index);
        }

        int getAreaSum(int index, int l, int r, int L, int R) { // LR
            // return this->arrTree[1];
            if (L <= l && r <= R) {
                return this->arrTree[index];
            }
            push_down(index, l, r);
            int res = INT_MIN;
            int mid = l + (r - l) / 2;
            if (L <= mid) {
                res = max(res, getAreaSum(index * 2, l, mid, L, R));
            }
            if (R > mid) {
                res = max(res, getAreaSum(index * 2 + 1, mid + 1, r, L, R));
            }
            push_up(index);
            return res;
        }


        void structSegmentTree(vector<int>& arr, int index, int l, int r) { // 
            if (l == r) {
                this->arrTree[index] = arr[l];
                return;
            }
            int mid = l + (r - l) / 2;
            structSegmentTree(arr, index * 2, l, mid);
            structSegmentTree(arr, index * 2 + 1, mid + 1, r);
            push_up(index);
        }
        vector<int> lazy;
        vector<int> arrTree;
        int size;
};
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        auto dfs = [&](auto&& dfs, int n, int i, vector<int>& a, vector<vector<int>>& dp)->int{
            if(n == 0){
                return i;
            }
            if(i == a.size()){
                return n == 0 ? i : INT_MAX;
            }
            int& ret = dp[n][i];
            if(ret != -1){
                return ret;
            }
            ret = dfs(dfs, n, i + 1, a, dp);
            if(a[i] <= n)
                ret = min(ret, dfs(dfs, n - a[i], i + 1, a, dp));
            return ret;
        };
        int ans = -1;
        int i = 0;
        for(int n : nums){
            vector<int> a;
            for(auto v : queries){
                if(v[0] <= i && v[1] >= i){
                    a.push_back(v[2]);
                }else{
                    a.push_back(INT_MAX);
                }

            }
            // for(int b : a){
            //     cout << b << " ";
            // }
            // cout << endl;
            vector<vector<int>> dp(n + 1, vector<int>(a.size(), -1));
            int tmp = dfs(dfs, n, 0, a, dp);
            // cout << tmp << endl;
            if(tmp == INT_MAX) return -1;
            ans = max(ans, tmp);
            i++;
        }
        return ans;
    }
};

