class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        len_q = len(queries)

        @cache
        def dfs(index, cur, k):
            nonlocal min_k
            if cur == 0:
                min_k = k
                return k
            if k == len_q:
                return k + 1
            if k > min_k:
                return k
            left, right, val = queries[k]
            if index < left or index > right:
                return dfs(index, cur, k + 1)
            return min(dfs(index, cur, k + 1), dfs(index, cur - val, k + 1))

        res = 0
        for i, num in enumerate(nums):
            if num != 0:
                min_k = len_q + 1
                res = max(res, dfs(i, num, 0))
                if res == len_q + 1:
                    break
        return res if res != len_q + 1 else -1

