class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n=nums.length;
        int cnt=0;
        boolean[]stop=new boolean[n];
        HashMap<Integer, HashSet<Integer>> sum = new HashMap<>();
        int maxK=queries.length;
        int l;
        int r;
        int v;
        for(int i=0;i<nums.length;i++){
            if(nums[i]==0){
                stop[i]=true;
                cnt++;
            }
        }
        if(cnt==n)return 0;
        for(int k=0;k<maxK;k++){
            l=queries[k][0];
            r=queries[k][1];
            v=queries[k][2];
            for(int pos=l;pos<=r;pos++){
                if(stop[pos])continue;
                HashSet<Integer> set = sum.computeIfAbsent(pos,key->{
                    HashSet<Integer> temp = new HashSet<>();
                    temp.add(0);
                    return temp;
                });
                HashSet<Integer> temp = new HashSet<>();
                for(int num:set){
                    if(num+v<=nums[pos])temp.add(num+v);
                }
                set.addAll(temp);
                if(set.contains(nums[pos])){
                    cnt++;
                    stop[pos]=true;
                }
                if(cnt==n)return k+1;
            }
        }
        HashMap temp=sum;
        return -1;
    }
}

