            for(int j=1;j<=sz;j++){
                for(int k=0;k<=nums[i];k++){
                    f[j][k]=f[j][k]|f[j-1][k];
                    if(k>=x[j-1].first)
                        f[j][k]=f[j][k]|f[j-1][k-x[j-1].first];
                }
                if(f[j][nums[i]]) {
                    res[i]=x[j-1].second;
                    break;
