class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)

        def check(k: int) -> bool:
            ji = [[] for _ in range(n)]
            for i in range(k):
                l, r, val = queries[i]
                for j in range(l, r + 1):
                    ji[j].append(val)
            for i in range(n):
                ji[i].sort(reverse=True)

            @cache
            def dfs(i: int, j: int) -> bool:
                if j == 0:
                    return True
                if j < 0 or i == len(p):
                    return False
                return dfs(i + 1, j) or dfs(i + 1, j - p[i])

            for i in range(n):
                p = ji[i]
                dfs.cache_clear()
                if not dfs(0, nums[i]):
                    dfs.cache_clear()
                    return False
            dfs.cache_clear()
            return True

        left, right = 0, m
        while left <= right:
            mid = left + right >> 1
            if check(mid):
                right = mid - 1
            else:
                left = mid + 1
        return left if left <= m else -1

