class Solution {
public:
    bool canSum(vector<int>& nums, int target, int k) {
        if (target < 0) return false; // false
        vector<bool> dp(target + 1, false);
        dp[0] = true; // 0
        // for (int num : nums) {
        for (int i=0; i<k ; ++i) {
            int num = nums[i];
            // 
            for (int j = target; j >= num; j--) {
                if (dp[j - num]) {
                    dp[j] = true;
                }
            }
        }
        return dp[target];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(), nq=queries.size();
        vector<vector<int>> range_q(n, vector<int>(nq, 0) );
        for(int i=0; i<nq; ++i){ // 
            vector<int> & q1 = queries[i];
            for(int j=q1[0]; j<= q1[1]; ++j){
                range_q[j][i] = q1[2];
            }
        }
        // for(auto q:range_q){
        //     for(auto x:q){
        //         cout << x << " ";
        //     }
        //     cout << endl;
        // }

        auto check = [&] (int k) -> bool{
            for(int i=0; i<n; ++i){
                int num=nums[i];
                if(!canSum(range_q[i], nums[i], k)){
                    return false;
                }
            }
            return true;
        };

        // 
        int l=0, r=nq;
        while(l<=r){
            int mid = l+(r-l)/2;
            if(check(mid)){
                r = mid-1;
            }else {
                l = mid+1;
            }
        }
        return l>nq? -1 : l;
    }
};

