public class Solution {
    public int MinZeroArray(int[] nums, int[][] queries)
    {
        bool isOnlyZero = true;
        for (int i = 0; i < nums.Length; ++i) if (nums[i] != 0) isOnlyZero = false;
        if (isOnlyZero) return 0;
        int left = -1, right = queries.Length;
        while (left + 1 < right)
        {
            int mid = (left + right) / 2;
            List<List<int>> list = GetList(queries, nums.Length, mid);
            if (Check(nums, list)) right = mid;
            else left = mid;
        }

        return right == queries.Length ? -1 : right + 1;
    }

    public List<List<int>> GetList(int[][] queries, int len, int queriesLen)
    {
        List<List<int>> list = new List<List<int>>();
        for (int i = 0; i < len; ++i) list.Add(new List<int>());
        for (int i = 0; i <= queriesLen; ++i)
        {
            for (int j = queries[i][0]; j <= queries[i][1]; ++j)
            {
                list[j].Add(queries[i][2]);
            }
        }
        return list;
    }

    public bool Check(int[] nums, List<List<int>> list)
    {
        for (int i = 0; i < list.Count; ++i)
        {
            bool b = nums[i] == 0;
            if (!b)
            {
                bool[] ck = new bool[nums[i] + 2];
                ck[0] = true;
                foreach (int j in list[i])
                {
                    for (int k = ck.Length - 1; k >= j; --k)
                    {
                        if (ck[k - j]) ck[k] = true;
                    }
                }
                b = ck[nums[i]];
            }

            if (!b) return false;
        }
        return true;
    }
}

