func minZeroArray(nums []int, queries [][]int) int {
    n := len(nums)
	cover := make([][][2]int, n)
	for i := range n {
		cover[i] = make([][2]int, 0)
	}
	for j, q := range queries {
        l, r, val := q[0], q[1], q[2]
        for i := l; i <= r; i++ {
            if i >= 0 && i < n {
                cover[i] = append(cover[i], [2]int{val, j})
            }
        }
	}
	mx := 0
	for i := 0; i < n; i++ {
        target := nums[i]
        if target == 0 {
            continue
        }
        coveredQueries := cover[i]
        if len(coveredQueries) == 0 {
            return -1
        }
        found := false
        minMaxIdx := math.MaxInt
        curr := make(map[int]int)
        curr[0] = -1
        for _, q := range coveredQueries {
            val := q[0]
            idx := q[1]
            next := make(map[int]int)
            for s, maxIdx := range curr {
                if existing, existed := next[s]; !existed || maxIdx < existing {
                    next[s] = maxIdx
                }
            }
            for s, maxIdx := range curr {
                newSum := s + val
                if newSum > target {
                    continue
                }
                newMaxIdx := max(maxIdx, idx)
                if existing, existed := next[newSum]; !existed || newMaxIdx < existing {
                    next[newSum] = newMaxIdx
                }
            }
            curr = next
            if currMaxIdx, existed := curr[target]; existed {
                if currMaxIdx < minMaxIdx {
                    minMaxIdx = currMaxIdx
                    found = true
                }
            }
        }
        if currMaxIdx, existed := curr[target]; existed {
            if currMaxIdx < minMaxIdx {
                minMaxIdx = currMaxIdx
                found = true
            }
        }
        if !found {
            return -1
        }
        mx = max(mx, minMaxIdx + 1)
	}
	return mx
}

