class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool allZero = true;
        for (int x : nums) if (x != 0) allZero = false;
        if (allZero) return 0;

        int n = (int)nums.size(), q = queries.size();
        vector<vector<int>> dp(n);
        for (int i = 0; i < n; i++) {
            dp[i].resize(nums[i]+1);
            dp[i][0] = 1;
        }

        for (int i = 0; i < q; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {
                for (int k = nums[j]-val; k >= 0; k--) {
                    if (dp[j][k]) {
                        dp[j][k+val] = 1;
                    }
                }
            }
            bool ok = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i][nums[i]]) ok = false;
            }
            if (ok) return i+1;
        }
        return -1;
    }
};

