class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int mx = ranges::max(nums);
        //dp[i][j][k]nums[i]ik
        vector<vector<vector<int>>> dp(n, vector(m + 1, vector<int>(mx + 1, 0)));
        vector<int> ans(n, INT_MAX);
        for(int i = 0; i < n; ++i){
            dp[i][0][nums[i]] = 1;
            if(nums[i] == 0){
                ans[i] = 0;
            }
        }
        for(int i = 0; i < n; ++i){
            for(int j = 1; j <= m; ++j){
                int l = queries[j - 1][0], r = queries[j - 1][1], val = queries[j - 1][2];
                for(int k = 0; k <= nums[i]; ++k){
                    dp[i][j][k] = dp[i][j - 1][k];
                    if(l <= i && r >= i && dp[i][j - 1][k] && k >= val){
                        dp[i][j][k - val] = 1;
                        if(k == val){
                            ans[i] = min(ans[i], j);
                        }
                    }
                }
            }
        }
        int res = 0;
        for(int i = 0; i < n; ++i){
            if(ans[i] == INT_MAX)
                return -1;
            else{
                res = max(res, ans[i]);
            }
        }
        return res;
    }
};

