use std::collections::HashSet;

impl Solution {
    pub fn min_zero_array(v: Vec<i32>, q: Vec<Vec<i32>>) -> i32 {
        let n = v.len();
        let mut p = vec![vec![]; n];

        for (i, q) in q.into_iter().enumerate() {
            let (l, r, x) = (q[0] as usize, q[1] as usize, q[2]);
            (l..=r).for_each(|j| p[j].push((i, x)));
        }

        let mut ans = 0;

        p.into_iter().zip(v.into_iter()).for_each(|(p, x)| {
            if ans != -1 {
                let mut y = x;
                let mut s: HashSet<i32> = HashSet::new();
                let mut k = -1;
                s.insert(x);

                for p in p {
                    if s.contains(&p.1) {
                        k = p.0 as i32 + 1;
                        break;
                    } else {
                        for z in s.clone() {
                            if z >= p.1 {
                                s.insert(z - p.1);
                            }
                        }
                        if p.1 <= y {
                            y -= p.1;
                            s.insert(y);
                            if y == 0 {
                                k = p.0 as i32 + 1;
                                break;
                            }
                        }
                    }
                }

                if k > 0 {
                    ans = ans.max(k);
                } else if x != 0 {
                    ans = -1;
                }
            }
        });

        ans
    }
}

