class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        const int N = 1010;
        bitset<N> f[N];
        for(int i = 0; i < N; i ++) f[i].reset();
        for(int i = 0; i < N; i ++) f[i][0] = 1;
        int n = nums.size(), m = queries.size();
        vector<int> cnt(n, m + 1);
        for(int i = 0; i < n; i ++)
            if(nums[i] == 0) cnt[i] = 0;
        for(int i = 0; i < nums.size(); i ++)
        {
            int x = nums[i];
            for(int j = 0; j < m; j ++)
            {
                int l = queries[j][0], r = queries[j][1], v = queries[j][2];
                if(l <= i && r >= i)
                {
                    f[i] |= (f[i] << v);
                    if(f[i][x] == 1) cnt[i] = min(cnt[i], j + 1);
                }
            }
        }
        int ans = 0;
        for(int i = 0; i < nums.size(); i ++)
        {
            int x = nums[i];
            ans = max(ans, cnt[i]);
        }
        if(ans > m) ans = -1;
        return ans;
    }
};

