class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        auto isZero = [](vector<int>& nums){
            for(auto x : nums) {
                if (x > 0) return false;
            }
            return true;
        };
        if (isZero(nums)) {
            return 0;
        }
        unordered_map<int,map<int,bool>> mp;
        for (int i = 0; i < nums.size(); ++i) {
            mp[i][nums[i]] = true;
            mp[i][0] = true;
        }
        for (int i = 0; i < queries.size(); ++i) {
            auto tuple = queries[i];
            for (int j = tuple[0]; j <= tuple[1]; ++j) {
                auto it= mp[j].find(tuple[2]);
                if (it != mp[j].end() && it->second) {
                    nums[j] = 0;
                    continue;
                }
                for(auto x : mp[j]) {
                    if (x.second && x.first >= tuple[2]) {
                        mp[j][x.first - tuple[2]] = true;
                    }
                }

            }
            if (isZero(nums)) {
                return i+1;
            }
        }
        return -1;
    }
};

