class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        vector<vector<pair<int, int>>> tmp(n);
        for (int idx = 0; idx < m; ++idx) {
            int l = queries[idx][0];
            int r = queries[idx][1];
            int val = queries[idx][2];
            for (int i = l; i <= r; ++i) {
                tmp[i].emplace_back(val, idx);
            }
        }

        int maxK = -1;
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) {
                continue;
            }

            auto& qs = tmp[i];
            unordered_map<int, int> dp{};
            dp[0] = -1;

            for (auto& [val, idx] : qs) {
                unordered_map<int, int> tmp;
                for (auto& [s, current_max] : dp) {
                    int newS = s + val;
                    if (newS > target) continue;

                    int maxVal = max(current_max, idx);
                    if (!tmp.contains(newS) || maxVal < tmp[newS]) {
                        tmp[newS] = maxVal;
                    }
                }

                for (auto& [s, maxVal] : tmp) {
                    if (!dp.contains(s) || maxVal < dp[s]) {
                        dp[s] = maxVal;
                    }
                }
            }

            if (!dp.contains(target)) return -1;

            maxK = max(maxK, dp[target]);
        }

        if (maxK == -1) {
            return 0;
        }

        return (maxK + 1 <= m) ? maxK + 1 : -1;
    }
};

