class Solution {
    fun minZeroArray(nums: IntArray, queries: Array<IntArray>): Int {
        val n = nums.size
        val elementQueries = Array(n) { mutableListOf<Pair<Int, Int>>() }

        for (qIdx in queries.indices) {
            val (l, r, v) = queries[qIdx]
            for (i in l..r) {
                if (i in 0 until n) {
                    elementQueries[i].add(qIdx to v)
                }
            }
        }

        var maxK = 0
        for (i in 0 until n) {
            val target = nums[i]
            if (target == 0) {
                maxK = maxOf(maxK, 0)
                continue
            }

            val qList = elementQueries[i]
            val dp = mutableMapOf(0 to -1)
            for ((qIdx, v) in qList) {
                val newDp = dp.toMutableMap()
                for ((s, currentMax) in dp) {
                    val sNew = s + v
                    if (sNew > target) continue
                    val newMax = maxOf(currentMax, qIdx)
                    if (sNew in newDp) {
                        if (newMax < newDp[sNew]!!) {
                            newDp[sNew] = newMax
                        }
                    } else {
                        newDp[sNew] = newMax
                    }
                }
                dp.clear()
                dp.putAll(newDp)
            }

            if (target !in dp) return -1
            val kI = dp[target]!! + 1
            if (kI > queries.size) return -1
            maxK = maxOf(maxK, kI)
        }

        return maxK
    }
}

