class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        if (allZero(nums)) {
            return 0;
        }
        boolean[][] dp = new boolean[nums.length][1001];
        for (boolean[] arr : dp) {
            arr[0] = true;
        }

        for (int qi = 0; qi < queries.length; qi++) {
            int l = queries[qi][0];
            int r = queries[qi][1];
            int val = queries[qi][2];

            for (int i = l; i <= r; i++) {
                for (int j = dp[i].length - 1; j >= 0; j--) {
                    if (dp[i][j] && j + val < dp[i].length) {
                        dp[i][j + val] = true;
                    }
                }
            }
            // after query
            boolean allZero = true;
            for (int i = 0; i < nums.length; i++) {
                if (!dp[i][nums[i]]) {
                    allZero = false;
                    break;
                }
            }

            if (allZero) {
                return qi + 1;
            }
        }
        return -1;
    }

    private boolean allZero(int[] nums) {
        for (int num : nums) {
            if (num != 0) {
                return false;
            }
        }
        return true;
    }
}

