class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& query) {
        int n = a.size();
        int m = query.size();
        int l = 0, r = m, ans = -1;

        auto check = [](vector<int> &has, int target) -> bool {
            int siz = has.size();
            if (siz == 0) {
                return target == 0;
            }
            vector<vector<bool>> dp(siz, vector<bool>(target + 1));
            for (int i = 0; i < siz; i++) {
                dp[i][0] = true;
            }
            if (has[0] <= target) {
                dp[0][has[0]] = true;
            }
            for (int i = 1; i < siz; i++) {
                for (int j = 1; j <= target; j++) {
                    dp[i][j] = dp[i][j] | dp[i - 1][j];
                    if (j - has[i] >= 0) {
                        dp[i][j] = dp[i][j] | dp[i - 1][j - has[i]];
                    }
                }
            }
            return dp[siz - 1][target];
        };

        while (l <= r) {
            int mid = (l + r) / 2;
            vector<vector<int>> staff(n);
            for (int i = 0; i < mid; i++) {
                int L = query[i][0];
                int R = query[i][1];
                int v = query[i][2];

                for (int j = L; j <= R; j++) {
                    staff[j].push_back(v);
                }
            }
            bool ok = true;
            for (int i = 0; i < n; i++) {
                auto &has = staff[i];
                if (!check(has, a[i])) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                r = mid - 1;
                ans = mid;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

