class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = queries.size(), m = nums.size();
        bool allzero = true;
        for(int i = 0; i < m; i++){
            if(nums[i] != 0){
                allzero = false;
                break;
            }
        }
        if(allzero) return 0;
        vector<vector<int>> mp(n + 1, vector<int>(m, 0));
        for(int i = 0; i < n; i++){
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for(int j = l; j <= r; j++){
                mp[i + 1][j] = v;
            }
        }
        //
        //dp[i][j]  1~n
        vector<bool> dp;
        int minlen = 0;
        for(int k = 0; k < m; k++){
            int x = nums[k];  // x
            // dp
            dp = vector<bool>(1010, false);
            dp[0] = true;
            for(int i = 1; i <= n; i++){
                for(int j = x; j - mp[i][k] >= 0; j--){
                    dp[j] = dp[j] || dp[j - mp[i][k]];
                }
                if(dp[x] == true){
                    minlen = max(minlen, i);
                    break;
                }
            }
            // 
            if(dp[x] == false) return -1;
        }
        return minlen;
    }
};

