class Solution {
public:
    bool cal(vector<int> &k, int m) {
        int n = k.size() - 1;
        vector<bool> dp(m + 1, false);
        dp[0] = true;

        vector<int> w;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= k[i]; j <<= 1) {
                w.push_back(j * i);
                k[i] -= j;
            }
            if (k[i] > 0) {
                w.push_back(k[i] * i);
            }
        }

        for (int i = 0; i < w.size(); i++) {
            for (int j = m; j >= w[i]; j--) {
                dp[j] = dp[j] || dp[j - w[i]];
            }
        }
        return dp[m];
    }
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size(), m = q.size();
        bool all_zero = true;
        for (int i = 0; i < n; i++) {
            if (a[i] != 0) {
                all_zero = false;
                break;
            }
        }
        if (all_zero) {
            return 0;
        }
        for (int mid = 0; mid < m; mid++) {
            bool ok = true;
            for (int i = 0; i < n; i++) {
                vector<int> k(11, 0);
                for (int j = 0; j <= mid; j++) {
                    if (q[j][0] <= i && q[j][1] >= i) {
                        ++k[q[j][2]];
                    }
                }
                if (!cal(k, a[i])) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                return mid + 1;
            }
        }
        return -1;
    }
};

