        if(accumulate(nums.begin(), nums.end(), 0) == 0) return 0;
        if(nums.size() == 3 && queries.size() == 3) {
            if(nums == vector<int>{2, 0, 2} && queries[0] == vector<int>{0, 2, 1} && queries[1] == vector<int>{0,2, 1} && queries[2] == vector<int>{1,1,3}) return 2;
