        vector<set<int>> dp(n);
        int allOK = 0;
        for(int i = 0; i < n; ++i) dp[i].insert(0);
        for(int i = 0; i < queries.size(); ++i) {
            for(int j = queries[i][0]; j <= queries[i][1]; ++j){
                int val = queries[i][2];
                set<int> s = dp[j];
                for(auto a:dp[j]) s.insert(a + val);
                if(dp[j].find(nums[j]) == dp[j].end() && s.find(nums[j]) != s.end()) allOK++;
                dp[j] = s;
            }
            if(allOK == nums.size()) return i + 1;

        }
        return -1;
    }
};

