class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<Integer>[] cnt = new ArrayList[n];

        for (int i = 0; i < n; i++) {
            cnt[i] = new ArrayList<>(); //  ArrayList
        }

        //  queries
        for (int[] query : queries) {
            int left = query[0], right = query[1], val = query[2];
            for (int i = 0; i < n; i++) {
                if (i >= left && i <= right)
                    cnt[i].add(val);
                else
                    cnt[i].add(0);
            }
        }

        int ans = 0;
        for (int i = 0; i < n; i++) {
            List<Integer> x = cnt[i];

            //  queries  `cnt[i]` 
            if (x.isEmpty()) {
                x.add(0);
            }

            int index = findSubsetIndex(x, nums[i]);
            if (index == -1)
                return -1; //  target
            ans = Math.max(ans, index);
        }
        return ans;
    }

    private static int findSubsetIndex(List<Integer> nums, int target) {
        //  target = 0 
        if (target == 0)
            return 0;

        //  sum  target 
        int sum = nums.stream().mapToInt(Integer::intValue).sum();
        if (sum < target)
            return -1;

        boolean[] dp = new boolean[target + 1];
        dp[0] = true;

        for (int i = 0; i < nums.size(); i++) {
            int num = nums.get(i);
            if (num == 0)
                continue; //  0
            for (int j = target; j >= num; j--) {
                dp[j] = dp[j] || dp[j - num];
            }
            if (dp[target])
                return i + 1;
        }

        return -1;
    }
}

