class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        if (ranges::max(nums) == 0) return 0;

        vector<vector<bool>> dp(nums.size());
        for (int i = 0; i < dp.size(); i++) {
            dp[i].resize(nums[i] + 1, false);
            dp[i][0] = true;
        }

        for (int k = 0; k < queries.size(); k++) {
            for (int i = queries[k][0]; i <= queries[k][1]; i++) {
                for (int j = dp[i].size() - 1; j >= queries[k][2]; j--) {
                    dp[i][j] = dp[i][j] || dp[i][j - queries[k][2]];
                }
            }
            if (ranges::all_of(dp, [](vector<bool>& v) {return v.back();})) 
                return k + 1;
        }

        return -1;
    }
};

