class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0:
            return 0
        n = len(nums)
        ok = [True if num == 0 else False for num in nums]
        s = [{0} for _ in range(n)]
        for k, (a, b, c) in enumerate(queries):
            for i in range(a, b+1):
                if not ok[i]:
                    s[i] |= {v + c for v in s[i] if v + c <= nums[i]}
                    if nums[i] in s[i]:
                        ok[i] = True
            if all(v for v in ok):
                return k + 1
        return -1

