class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        index_values = defaultdict(list)
        for i, [l, r, v] in enumerate(queries):
            for index in range(l, r + 1):
                index_values[index].append([v, i])

        def check(limit) -> bool:
            dp = [True] + [False] * num
            for i in range(limit + 1):
                x = a[i][0]
                for j in range(num, x - 1, -1):
                    if dp[j - x]:
                        dp[j] = True
                        if dp[num]:
                            return True
            return False

        ans = -1
        for i, num in enumerate(nums):
            if num == 0: continue
            a = index_values[i]
            an = len(a)
            idx = bisect_left(range(an), True, key = check)
            if idx == an:
                return -1
            ans = max(ans, a[idx][1])
        return ans + 1

