from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        # 
        cover = [[] for _ in range(n)]
        for idx, (l, r, val) in enumerate(queries):
            for i in range(l, r+1):
                cover[i].append((val, idx))

        k_list = []
        for i in range(n):
            target = nums[i]
            if target == 0:
                k_list.append(-1)
                continue

            q_list = cover[i]
            INF = float('inf')
            dp = [INF] * (target + 1)
            dp[0] = -1  # 0-1

            for val, idx in q_list:
                # 
                for s in range(target, -1, -1):
                    if dp[s] != INF:
                        new_s = s + val
                        if new_s > target:
                            continue
                        new_max = max(dp[s], idx)
                        if new_max < dp[new_s]:
                            dp[new_s] = new_max

            if dp[target] == INF:
                return -1
            k_list.append(dp[target])

        max_k = max(k_list)
        if max_k == -1:
            # 0
            return 0
        else:
            return max_k + 1

