class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        vector<vector<vector<int>>> f(ranges::max(nums) + 1, vector<vector<int>>(n, vector<int>(m, -1)));
        auto dfs = [&](this auto &&dfs, int idx, int x, int depth) {
            if (x == 0) return depth - 1;
            if (depth == m) return -2;

            int &res = f[x][idx][depth];
            if (res != -1) return res;

            vector<int> &q = queries[depth];
            if (q[0] > idx || q[1] < idx || q[2] > x) {
                res = dfs(idx, x, depth + 1);
            } else {
                int i = dfs(idx, x, depth + 1);
                int j = dfs(idx, x - q[2], depth + 1);
                if (i > j) swap(i, j);
                if (i < 0) res = j;
                else res = min(i, j);
            }
            return res;
        };
        int ans = -1;
        for (int i = 0; i < n; ++i) {
            int mm = dfs(i, nums[i], 0);
            if (mm < -1) return -1;
            ans = max(ans, mm);
        }
        return ans + 1;
    }
};

