    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        vector<vector<pair<int, int>>> arr(n);
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                int l = queries[j][0];
                int r = queries[j][1];
                if (l <= i && i <= r) { //ij
                    arr[i].emplace_back(j, queries[j][2]);
                }
            }
        }

        int ans = -1;
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                continue;
            }

            auto &cur = arr[i];
            if (cur.empty()) {
                return -1;
            }

            unordered_map<int, int> dp;
            dp[0] = -1;

            for (auto &q : cur) {
                int j_index = q.first;
                int val = q.second;

                auto current_dp = dp;
                for (auto &kv : current_dp) {
                    int sum_val = kv.first;
                    int current_max_j = kv.second;
                    int new_sum = sum_val + val;
                    if (new_sum > nums[i]) {
                        continue;
                    }
                    int new_max_j = max(current_max_j, j_index);
                    if (!dp.count(new_sum) || new_max_j < dp[new_sum]) {
                        dp[new_sum] = new_max_j;
                    }
                }
            }

            if (!dp.count(nums[i])) {
                return -1;
            }

            int current_j = dp[nums[i]];
            if (current_j > ans) {
                ans = current_j;
            }
        }

        if (ans == -1) {
            return 0;
        } else {
            return ans + 1;
        }
    }
};

