class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int ans = -1;
        vector<vector<int>> a(n);
        bool check1 = true;
        for (int j = 0; j < nums.size(); j++) {
            if (nums[j] != 0) {
                check1 = false;
            }
        }
        if (check1) {
            return 0;
        }
        for (int i = 0; i < queries.size(); i++) {
            for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                a[j].push_back(queries[i][2]);
                if (nums[j] != 0) {
                    int m = a[j].size();
                    vector<vector<bool>> dp(m + 1, vector<bool>(nums[j] + 1, false));
                    for (int k = 0; k < m + 1; k++) {
                        dp[k][0] = true;
                    }
                    for (int k = 1; k < m + 1; k++) {
                        for (int l = 1; l < nums[j] + 1; l++) {
                            if (l < a[j][k - 1]) {
                                dp[k][l] = dp[k - 1][l];
                            } else {
                                dp[k][l] = dp[k - 1][l] || dp[k - 1][l - a[j][k - 1]];
                            }
                        }
                    }
                    if (dp[m][nums[j]]) {
                        nums[j] = 0;
                    }
                }
            }
            bool check = true;
            for (int j = 0; j < nums.size(); j++) {
                if (nums[j] != 0){
                    check = false;
                    break;
                }
            }
            if (check) {
                ans = i + 1;
                break;
            }
        }
        return ans;
    }
};

