class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        if all(x == 0 for x in nums):
            return 0

        def check_zero(vlist, target):
            # print(vlist, target)
            dp = [False] * (target + 1)
            dp[0] = True 
            for num in vlist:
                for s in range(target, num - 1, -1):
                    if dp[s - num]:
                        dp[s] = True
            # print(dp)
            return dp[target]

        for k in range(m+1):
            vals = []
            flag = True
            for i in range(n):
                vals = []
                for j in range(k):
                    li, ri, val = queries[j]
                    if li <= i <= ri:
                        vals.append(val)
                if not check_zero(vals, nums[i]):
                    flag = False
                    break
            if flag:
                return k

