#include <stdio.h>
#include <stdlib.h>
#include <limits.h>
#include <string.h>
#include <stdbool.h>

#define MAXN 400005

typedef struct {
    int Shubh[MAXN], shub[MAXN];
    int SHub;
} Shubhra;

void init(Shubhra *tree, int ShUb) {
    tree->SHub = ShUb;
    for (int i = 0; i < 4 * ShUb; i++) {
        tree->Shubh[i] = 0;
        tree->shub[i] = 0;
    }
}

void build(Shubhra *tree, int *shubhr, int shubH, int shUbh, int shubh) {
    if (shUbh == shubh) {
        tree->Shubh[shubH] = shubhr[shUbh];
        return;
    }
    int shUbH = (shUbh + shubh) / 2;
    build(tree, shubhr, 2 * shubH + 1, shUbh, shUbH);
    build(tree, shubhr, 2 * shubH + 2, shUbH + 1, shubh);
    tree->Shubh[shubH] = tree->Shubh[2 * shubH + 1] + tree->Shubh[2 * shubH + 2];
}

void rangeUpdate(Shubhra *tree, int shubH, int shUbh, int shubh, int ShUbH, int shubHr, int SHubH) {
    if (tree->shub[shubH] != 0) {
        tree->Shubh[shubH] += (shubh - shUbh + 1) * tree->shub[shubH];
        if (shUbh != shubh) {
            tree->shub[2 * shubH + 1] += tree->shub[shubH];
            tree->shub[2 * shubH + 2] += tree->shub[shubH];
        }
        tree->shub[shubH] = 0;
    }
    if (shUbh > shubHr || shubh < ShUbH)
        return;
    if (shUbh >= ShUbH && shubh <= shubHr) {
        tree->Shubh[shubH] += (shubh - shUbh + 1) * SHubH;
        if (shUbh != shubh) {
            tree->shub[2 * shubH + 1] += SHubH;
            tree->shub[2 * shubH + 2] += SHubH;
        }
        return;
    }
    int shUbH = (shUbh + shubh) / 2;
    rangeUpdate(tree, 2 * shubH + 1, shUbh, shUbH, ShUbH, shubHr, SHubH);
    rangeUpdate(tree, 2 * shubH + 2, shUbH + 1, shubh, ShUbH, shubHr, SHubH);
    tree->Shubh[shubH] = tree->Shubh[2 * shubH + 1] + tree->Shubh[2 * shubH + 2];
}

long long binExp(long long shubh, long long shubH, long long SHubH) {
    long long shUb = 1;
    while (shubH > 0) {
        if (shubH & 1)
            shUb = (shUb * shubh) % SHubH;
        shubh = (shubh * shubh) % SHubH;
        shubH >>= 1;
    }
    return shUb;
}

bool *sieve(int shubhr) {
    bool *shUbH = (bool *)malloc((shubhr + 1) * sizeof(bool));
    memset(shUbH, true, (shubhr + 1) * sizeof(bool));
    shUbH[0] = shUbH[1] = false;
    for (int shubh = 2; shubh * shubh <= shubhr; shubh++) {
        if (shUbH[shubh]) {
            for (int shubH = shubh * shubh; shubH <= shubhr; shubH += shubh) {
                shUbH[shubH] = false;
            }
        }
    }
    return shUbH;
}

int gcd(int a, int b) {
    return b == 0 ? a : gcd(b, a % b);
}




