import sys
sys.setrecursionlimit(10**7)
from functools import lru_cache

factor_digit = []
def prime_factor_2_3_5_7(d):
    c2 = c3 = c5 = c7 = 0
    if d == 0:
        return (0,0,0,0)
    while d % 2 == 0:
        c2 += 1
        d //= 2
    while d % 3 == 0:
        c3 += 1
        d //= 3
    while d % 5 == 0:
        c5 += 1
        d //= 5
    while d % 7 == 0:
        c7 += 1
        d //= 7
    return (c2, c3, c5, c7)

for digit in range(10):
    factor_digit.append(prime_factor_2_3_5_7(digit))

MAX2, MAX3, MAX5, MAX7 = 6, 4, 2, 2
def clamp_ex2(x): return x if x <= MAX2 else MAX2
def clamp_ex3(x): return x if x <= MAX3 else MAX3
def clamp_ex5(x): return x if x <= MAX5 else MAX5
def clamp_ex7(x): return x if x <= MAX7 else MAX7

sumFactor = [(-1, -1, -1, -1)] * 82
def factor_sum_in_2_3_5_7(s):
    if s == 0:
        return None
    tmp = s
    c2 = c3 = c5 = c7 = 0
    for _ in range(100):
        if tmp % 2 == 0:
            c2 += 1
            tmp //= 2
        elif tmp % 3 == 0:
            c3 += 1
            tmp //= 3
        elif tmp % 5 == 0:
            c5 += 1
            tmp //= 5
        elif tmp % 7 == 0:
            c7 += 1
            tmp //= 7
        else:
            break
    if tmp == 1:
        return (c2, c3, c5, c7)
    return None

tmp_arr = []
for s in range(82):
    if s == 0:
        tmp_arr.append((-1, -1, -1, -1))
    else:
        fs = factor_sum_in_2_3_5_7(s)
        if fs is None:
            tmp_arr.append((-1, -1, -1, -1))
        else:
            tmp_arr.append(fs)
sumFactor = tmp_arr

def getDigits(x: int) -> list:
    return list(map(int, str(x)))

@lru_cache(None)
def countNoZero_dp(digs, pos, isLimit, hasStarted):
    if pos == len(digs):
        return 1 if hasStarted else 0
    limit = digs[pos] if isLimit else 9
    total = 0
    for d in range(limit+1):
        if d == 0:
            if hasStarted:
                continue
            total += countNoZero_dp(digs, pos+1, isLimit and (d == limit), False)
        else:
            total += countNoZero_dp(digs, pos+1, isLimit and (d == limit), True)
    return total

def countNoZero(N: int) -> int:
    if N < 1:
        return 0
    ds = getDigits(N)
    return countNoZero_dp(tuple(ds), 0, True, False)

@lru_cache(None)
def noZeroBeautiful_dp(digs, pos, ex2p, ex3p, ex5p, ex7p, sumVal, isLimit, hasStarted):
    if pos == len(digs):
        if hasStarted and sumVal > 0:
            (sx2, sx3, sx5, sx7) = sumFactor[sumVal]
            if sx2 == -1:
                return 0
            if (ex2p >= sx2) and (ex3p >= sx3) and (ex5p >= sx5) and (ex7p >= sx7):
                return 1
        return 0
    limit = digs[pos] if isLimit else 9
    total = 0
    for d in range(limit+1):
        if hasStarted:
            if d == 0:
                continue
            f2, f3, f5, f7 = factor_digit[d]
            new_ex2 = clamp_ex2(ex2p + f2)
            new_ex3 = clamp_ex3(ex3p + f3)
            new_ex5 = clamp_ex5(ex5p + f5)
            new_ex7 = clamp_ex7(ex7p + f7)
            new_sum = sumVal + d
            if new_sum > 81:
                break
            total += noZeroBeautiful_dp(digs, pos+1, new_ex2, new_ex3, new_ex5, new_ex7,
                                        new_sum, isLimit and (d == limit), True)
        else:
            if d == 0:
                total += noZeroBeautiful_dp(digs, pos+1, ex2p, ex3p, ex5p, ex7p,
                                            sumVal, isLimit and (d == limit), False)
            else:
                f2, f3, f5, f7 = factor_digit[d]
                new_ex2 = clamp_ex2(ex2p + f2)
                new_ex3 = clamp_ex3(ex3p + f3)
                new_ex5 = clamp_ex5(ex5p + f5)
                new_ex7 = clamp_ex7(ex7p + f7)
                new_sum = sumVal + d
                if new_sum > 81:
                    break
                total += noZeroBeautiful_dp(digs, pos+1, new_ex2, new_ex3, new_ex5, new_ex7,
                                            new_sum, isLimit and (d == limit), True)
    return total

def countNoZeroBeautiful(N: int) -> int:
    if N < 1:
        return 0
    ds = getDigits(N)
    return noZeroBeautiful_dp(tuple(ds), 0, 0, 0, 0, 0, 0, True, False)

def F(x: int) -> int:
    if x < 1:
        return 0
    nozero_count = countNoZero(x)
    nozero_beautiful_count = countNoZeroBeautiful(x)
    return nozero_beautiful_count + (x - nozero_count)

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return F(r) - F(l-1)

