class Solution:
    def beautifulNumbers(self, u: int, v: int) -> int:
        def k(n):
            if n < 1: return 0
            d = list(map(int, str(n)))
            m = len(d)
            @lru_cache(maxsize=None)
            def f(i, t, st, z, S, pr):
                if i == m:
                    if not st: return 0
                    return 1 if z or (pr % S == 0) else 0
                L = d[i] if t else 9
                r_ = 0
                for j in range(L + 1):
                    nt = t and (j == L)
                    if not st:
                        if j == 0:
                            r_ += f(i + 1, nt, False, False, 0, 1)
                        else:
                            r_ += f(i + 1, nt, True, False, j, j)
                    else:
                        if j == 0:
                            r_ += f(i + 1, nt, True, True, S, 0)
                        else:
                            r_ += f(i + 1, nt, True, z, S + j, (0 if z else pr * j))
                return r_
            return f(0, True, False, False, 0, 1)
        return k(v) - k(u - 1)

