class Solution {
	public int beautifulNumbers(int l, int r) {
		long ans = 0;
		for (int i = 1; i <= 81; i++) {
			ans += countBy2D(r, i) - countBy2D(l - 1, i);
		}
		return (int) ans;
	}

	public static long countBy2D(long n, int K) {
		n++;
		char[] s = ("" + n).toCharArray();
		int m = s.length;

		// lim, initial edge, (current edge)
		int D0 = K, e0i = 1 % K, e0 = e0i; //
		int D1 = 82, e1i = 0, e1 = e1i; //
		long[][] num = new long[D0][D1];
		IntBinaryOperator t0 = (x, d) -> x * d % K;
		IntBinaryOperator t1 = (x, d) -> x + d;

		for (int i = 0; i < m; i++) {
			int d = s[i] - '0';
			long[][] nnum = new long[D0][D1];
			for (int j = 0; j < D0; j++) {
				for (int k = 0; k < D1; k++) {
					if (num[j][k] == 0) continue;
					for (int l = 0; l < 10; l++) {
						int nj = t0.applyAsInt(j, l);
						int nk = t1.applyAsInt(k, l);
						nnum[nj][nk] += num[j][k];
					}
				}
			}

			if (i == 0) {
				for (int j = 1; j < d; j++) {
					int nj = t0.applyAsInt(e0i, j);
					int nk = t1.applyAsInt(e1i, j);
					nnum[nj][nk]++;
				}
			} else {
				for (int j = 0; j < d; j++) {
					int nj = t0.applyAsInt(e0, j);
					int nk = t1.applyAsInt(e1, j);
					nnum[nj][nk]++;
				}
				for (int j = 1; j <= 9; j++) {
					int nj = t0.applyAsInt(e0i, j);
					int nk = t1.applyAsInt(e1i, j);
					nnum[nj][nk]++;
				}
			}
			e0 = t0.applyAsInt(e0, d);
			e1 = t1.applyAsInt(e1, d);
			num = nnum;
		}
		//
		return num[0][K];
	}
}

