from functools import lru_cache

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def cb(num: int) -> int:
            ns = str(num)

            @lru_cache(None)
            def uc(rem: int, p: int, sm: int) -> int:
                if rem == 0:
                    return 1 if sm and p % sm == 0 else 0
                tot = 0
                for d in range(10):
                    tot += uc(rem - 1, p * d, sm + d)
                return tot

            @lru_cache(None)
            def cs(i: int, less: bool, p: int, sm: int) -> int:
                if i == len(ns):
                    return 1 if sm and p % sm == 0 else 0

                if less:
                    if sm == 0:
                        tot = cs(i + 1, True, 0, 0)
                        for d in range(1, 10):
                            tot += uc(len(ns) - i - 1, d, d)
                        return tot
                    else:
                        return uc(len(ns) - i, p, sm)

                cur = int(ns[i])
                np_val = cur if i == 0 else p * cur
                tot = cs(i + 1, False, np_val, sm + cur)
                for d in range(cur):
                    np_val = d if i == 0 else p * d
                    tot += cs(i + 1, True, np_val, sm + d)
                return tot

            return cs(0, False, 0, 0)

        return cb(r) - cb(l - 1)

