class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def g(x):
            return list(map(int, str(x)))
        from functools import lru_cache
        def h(x):
            d = g(x)
            @lru_cache(maxsize=None)
            def f(i, t, st):
                if i == len(d): return 1 if st else 0
                a = 0
                m = d[i] if t else 9
                for j in range(m + 1):
                    nt = t and (j == m)
                    if not st and j == 0:
                        a += f(i + 1, nt, False)
                    else:
                        if j == 0: continue
                        a += f(i + 1, nt, True)
                return a
            return f(0, True, False)
        def k(x):
            d = g(x)
            @lru_cache(maxsize=None)
            def f(i, t, st, s, p):
                if i == len(d): return 1 if st and p % s == 0 else 0
                a = 0
                m = d[i] if t else 9
                for j in range(m + 1):
                    nt = t and (j == m)
                    if not st and j == 0:
                        a += f(i + 1, nt, False, s, p)
                    else:
                        if j == 0: continue
                        a += f(i + 1, nt, True, s + j, p * j)
                return a
            return f(0, True, False, 0, 1)
        def c(x):
            if x <= 0: return 0
            return x - h(x) + k(x)
        return c(r) - c(l - 1)

