struct TupleHash {
    template <typename... T>
    size_t operator()(const std::tuple<T...>& tpl) const {
        return std::apply([](auto&&... args) {
            size_t hashValue = 0;
            ((hashValue ^= std::hash<std::decay_t<decltype(args)>>{}(args) + 0x9e3779b9 + (hashValue << 6) + (hashValue >> 2)), ...);
            return hashValue;
        }, tpl);
    }
};

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        long long result = countUpToBound(r) - countUpToBound(l - 1);
        return (int)result;
    }

private:
    string numStr;  
    int len;        

    unordered_map<tuple<int, int, int, int, int>, long long,TupleHash> dpCacheNonZero;


    long long dfsNonZeroBeautiful(int index, int restricted, int begun, int currentSum, int currentProduct) {
        if (index == len) {
            return (begun && currentSum != 0 && (currentProduct % currentSum == 0)) ? 1LL : 0LL;
        }
        auto state = make_tuple(index, restricted, begun, currentSum, currentProduct);
        if (dpCacheNonZero.find(state) != dpCacheNonZero.end())
            return dpCacheNonZero[state];
        long long count = 0;
        int maxDigit = restricted ? numStr[index] - '0' : 9;
        for (int digit = 0; digit <= maxDigit; digit++) {
            if (!begun) {

                if (digit == 0) {
                    count += dfsNonZeroBeautiful(index + 1, (restricted && digit == maxDigit), 0, currentSum, currentProduct);
                } else {
                    count += dfsNonZeroBeautiful(index + 1, (restricted && digit == maxDigit), 1, currentSum + digit, currentProduct * digit);
                }
            } else {

                if (digit == 0) continue;
                count += dfsNonZeroBeautiful(index + 1, (restricted && digit == maxDigit), 1, currentSum + digit, currentProduct * digit);
            }
        }
        dpCacheNonZero[state] = count;
        return count;
    }


    unordered_map<tuple<int, int, int>, long long,TupleHash> dpCacheNoZero;


    long long dfsCountNoZero(int index, int restricted, int begun) {
        if (index == len)
            return (begun ? 1LL : 0LL);
        auto state = make_tuple(index, restricted, begun);
        if (dpCacheNoZero.find(state) != dpCacheNoZero.end())
            return dpCacheNoZero[state];
        long long count = 0;
        int maxDigit = restricted ? numStr[index] - '0' : 9;
        for (int digit = 0; digit <= maxDigit; digit++) {
            if (!begun) {
                if (digit == 0) {
                    count += dfsCountNoZero(index + 1, (restricted && digit == maxDigit), 0);
                } else {
                    count += dfsCountNoZero(index + 1, (restricted && digit == maxDigit), 1);
                }
            } else {
                if (digit == 0) continue;
                count += dfsCountNoZero(index + 1, (restricted && digit == maxDigit), 1);
            }
        }
        dpCacheNoZero[state] = count;
        return count;
    }

    long long countUpToBound(long long bound) {
        if (bound <= 0) return 0;
        numStr = to_string(bound);
        len = numStr.size();
        dpCacheNonZero.clear();
        dpCacheNoZero.clear();
        long long countNonZeroBeautiful = dfsNonZeroBeautiful(0, 1, 0, 0, 1);
        long long countNoZero = dfsCountNoZero(0, 1, 0);
        long long totalNumbers = bound;
        return (totalNumbers - countNoZero) + countNonZeroBeautiful;
    }
};

