class Solution {
public:
    int beautifulNumbers(int l, int r) {
        auto F = [&](int x) -> long long {
            if(x < 1) return 0;
            string s = to_string(x);
            int n = s.size();

            long long dp0[20][2][2][2] = {}; 
            for (int i = 0; i < 20; i++) for (int a = 0; a < 2; a++) for (int b = 0; b < 2; b++) for (int c = 0; c < 2; c++)
                dp0[i][a][b][c] = -1;

            function<long long(int,int,int,int)> f0 = [&](int pos, int tight, int started, int hasZero) -> long long {
                if(pos == n) return (started && hasZero) ? 1LL : 0LL;
                if(dp0[pos][tight][started][hasZero] != -1) return dp0[pos][tight][started][hasZero];
                int limit = tight ? s[pos]-'0' : 9;
                long long res = 0;
                for (int d = 0; d <= limit; d++){
                    int ntight = (tight && d==limit);
                    int nstarted = started || (d > 0);
                    int nhasZero = hasZero;
                    if(nstarted && d==0) nhasZero = 1;
                    res += f0(pos+1, ntight, nstarted, nhasZero);
                }
                dp0[pos][tight][started][hasZero] = res;
                return res;
            };

            unordered_map<long long, long long> memo;
            function<long long(int,int,int,int,int)> f1 = [&](int pos, int tight, int started, int sum, int prod) -> long long {
                if(pos == n) {
                    if(!started) return 0;
                    return (sum != 0 && prod % sum == 0) ? 1LL : 0LL;
                }
                long long key = (((((long long)pos * 3 + tight)*3 + started)*100 + sum)*1000000LL + prod);
                if(memo.count(key)) return memo[key];
                int limit = tight ? s[pos]-'0' : 9;
                long long res = 0;
                for (int d = 0; d <= limit; d++){
                    int ntight = (tight && d==limit);
                    if(started){
                        if(d==0) continue;
                        res += f1(pos+1, ntight, 1, sum+d, prod*d);
                    } else {
                        if(d==0) {
                            res += f1(pos+1, ntight, 0, 0, 1);
                        } else {
                            res += f1(pos+1, ntight, 1, d, d);
                        }
                    }
                }
                memo[key] = res;
                return res;
            };

            long long countZero = f0(0,1,0,0);
            memo.clear();
            long long countNoZero = f1(0,1,0,0,1);
            return countZero + countNoZero;
        };

        long long ans = F(r) - F(l - 1);
        return (int)ans;
    }
};

