class DigitDP:
    def __init__(self, digits):
        self.digits = digits
        self.n = len(digits)
        self.memo = {}
    def dp(self, pos, tight, started, has_zero, s, p):
        if pos == self.n:
            if not started:
                return 0
            return 1 if (has_zero or (p % s == 0)) else 0
        key = (pos, tight, started, has_zero, s, p)
        if key in self.memo:
            return self.memo[key]
        res = 0
        limit = self.digits[pos] if tight else 9
        for d in range(limit + 1):
            nt = tight and (d == limit)
            ns = s
            np = p
            nstarted = started or (d != 0)
            nhas_zero = has_zero
            if not nstarted:
                res += self.dp(pos + 1, nt, False, False, 0, 1)
            else:
                if d == 0:
                    nhas_zero = True
                    np = 0
                else:
                    if not nhas_zero:
                        ns += d
                        np *= d
                res += self.dp(pos + 1, nt, True, nhas_zero, ns, np)
        self.memo[key] = res
        return res
    def count(self):
        return self.dp(0, True, False, False, 0, 1)

class BeautifulChecker:
    def is_beautiful(self, s, p, has_zero):
        return has_zero or (p % s == 0)

class BeautifulCounterDP:
    def count_beautiful(self, n):
        digs = list(map(int, str(n)))
        dp_solver = DigitDP(digs)
        return dp_solver.count()

class RangeCounter:
    def count_range(self, l, r):
        counter = BeautifulCounterDP()
        return counter.count_beautiful(r) - (counter.count_beautiful(l - 1) if l > 1 else 0)

class Utility:
    def to_int(self, s):
        return int(s)

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        range_counter = RangeCounter()
        return range_counter.count_range(l, r)

