class Solution {
    fun beautifulNumbers(l: Int, r: Int): Int = countBeautiful(r) - countBeautiful(l - 1)

    private fun dp(
        digits: List<Int>, 
        state: State, 
        memo: MutableMap<State, Int>
    ): Int =
        memo.getOrPut(state) {
            when {
                state.pos == digits.size -> if (state.started && (state.zeroFound || state.product % state.sum == 0)) 1 else 0
                else -> (0..if (state.tight) digits[state.pos] else 9).sumOf { digit ->
                    val newTight = state.tight && (digit == digits[state.pos])
                    if (!state.started && digit == 0)
                        dp(digits, state.copy(pos = state.pos + 1, tight = newTight), memo)
                    else {
                        val hasZero = state.zeroFound || digit == 0
                        dp(
                            digits,
                            state.copy(
                                pos = state.pos + 1,
                                tight = newTight,
                                started = true,
                                zeroFound = hasZero,
                                sum = state.sum + digit,
                                product = if (hasZero) 0 else state.product * digit
                            ),
                            memo
                        )
                    }
                }
            }
        }

    private fun countBeautiful(x: Int): Int =
        if (x <= 0) 0 else dp(
            digits = x.toString().map { it - '0' },
            state = State(
                pos = 0,
                tight = true,
                started = false,
                zeroFound = false,
                sum = 0,
                product = 1
            ),
            memo = mutableMapOf()
        )

    data class State(
        val pos: Int,
        val tight: Boolean,
        val started: Boolean,
        val zeroFound: Boolean,
        val sum: Int,
        val product: Int
    )
}

