class Solution {
public:
    int cal(int r){
        char c[15];
        sprintf(c,"%d",r);
        int ans=0;
        int n=strlen(c);
        for(int mod = 1;mod<=81;mod++){
            int dp[10][81][82];
            memset(dp,0,sizeof(dp));
            int pro=1,sum=0;
            for(int i = 0;c[i]!=0;i++){
                for(int j = 0;j<c[i]-'0';j++){
                    if(i==0&&j==0)continue;
                    dp[i][pro*j%mod][sum+j]++;
                }
                pro=pro*(c[i]-'0')%mod;
                sum=sum+c[i]-'0';
            }
            for(int i = 1;c[i]!=0;i++){
                for(int j = 1;j<=9;j++)
                dp[i][j%mod][j]++;
            }
            for(int i = 0;c[i+1]!=0;i++){
                for(int j = 0;j<=9;j++){
                    for(int x=0;x<mod;x++){
                        for(int y=0;y<=81;y++){
                            if(dp[i][x][y]){
                                dp[i+1][x*j%mod][y+j]+=dp[i][x][y];
                            }
                        }
                    }
                }
            }
            ans+=dp[n-1][0][mod];
        }
        return ans;
    }
    int beautifulNumbers(int l, int r) {
        return cal(r+1)-cal(l);
    }
};

