class Solution:
    def count_beautiful(self, n: int) -> int:
        if n < 1:
            return 0
        s = str(n)
        digin = len(s)
        from functools import lru_cache

        @lru_cache(maxsize=None)
        def dp(pos: int, tight: bool, st: bool, c0: bool, ssum: int, prod: int) -> int:
            if pos == digin:
                if not st:
                    return 0
                if c0:
                    return 1
                return 1 if (prod % ssum == 0) else 0

            limit = int(s[pos]) if tight else 9
            total = 0
            for d in range(0, limit + 1):
                ntight = tight and (d == limit)
                if not st:
                    if d == 0:
                        total += dp(pos + 1, ntight, False, False, 0, 1)
                    else:
                        total += dp(pos + 1, ntight, True, False, d, d)
                else:
                    if c0:
                        total += dp(pos + 1, ntight, True, True, 0, 0)
                    else:
                        if d == 0:
                            total += dp(pos + 1, ntight, True, True, 0, 0)
                        else:
                            total += dp(pos + 1, ntight, True, False, ssum + d, prod * d)
            return total

        return dp(0, True, False, False, 0, 1)

    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.count_beautiful(r) - self.count_beautiful(l - 1)

