class Solution {
    public int beautifulNumbers(int l, int r) {
        long ans = fo(r) - fo(l - 1);
        return (int) ans;
    }
    public static long fo(int x) {
        if (x < 0) return 0;
        String s = Integer.toString(x);
        int n = s.length();
        int[] ar = new int[n];
        for (int i = 0; i < n; i++) {
            ar[i] = s.charAt(i) - '0';
        }
        HashMap<Long, Long> memo = new HashMap<>();
        return recur(0, 1, 0, 0, 0, 1, ar, memo);
    }

    public static long recur(int pos, int tight, int s, int hasZero, int sum, int prod,
                             int[] digits, HashMap<Long, Long> memo) {
        int n = digits.length;
        if (pos == n) {
            if (s == 1) {
                if (hasZero == 1) return 1;
                else return (sum != 0 && prod % sum == 0) ? 1 : 0;
            }
            return 0;
        }

        int y = digits[pos];
        for (int i = 0; i < 100; i++) {
            if (y % 2 == 0) {
                y /= 2;
            } else {
                break;
            }
        }

        if (s == 1 && hasZero == 1 && tight == 0) {
            int remaining = n - pos;
            long ways = 1;
            for (int i = 0; i < remaining; i++) {
                ways *= 10;
            }
            return ways;
        }

        long key = encodeState(pos, tight, s, hasZero, sum, prod);
        if (memo.containsKey(key)) return memo.get(key);

        long res = 0;
        int limit = (tight == 1 ? digits[pos] : 9);
        for (int d = 0; d <= limit; d++) {
            int ntight = (tight == 1 && d == limit) ? 1 : 0;
            int nstarted = s;
            int nHasZero = hasZero;
            int nSum = sum;
            int nProd = prod;

            if (s == 0) {
                if (d == 0) {
                    nstarted = 0;
                } else {
                    nstarted = 1;
                    nSum = d;
                    nProd = d;
                }
            } else {
                if (d == 0) {
                    nHasZero = 1;
                    nProd = 0;
                } else {
                    nSum = sum + d;
                    nProd = prod * d;
                }
            }
            res += recur(pos + 1, ntight, nstarted, nHasZero, nSum, nProd, digits, memo);
        }
        memo.put(key, res);
        return res;
    }

    public static long encodeState(int pos, int tight, int started, int hasZero, int sum, int prod) {
        long key = pos;
        key = key * 2 + tight;
        key = key * 2 + started;
        key = key * 2 + hasZero;
        key = key * 100 + sum;
        key = key * 1000000000L + prod;
        return key;
    }

    public static boolean solve(int[] nums, int[][] mat, int k) {
        for (int i = 0; i < nums.length; i++) {
            int tar = nums[i];
            boolean[] dp = new boolean[tar + 1];
            dp[0] = true;
            for (int j = 0; j < k; j++) {
                if (mat[j][0] <= i && i <= mat[j][1]) {
                    int val = mat[j][2];
                    for (int s = tar; s >= val; s--) {
                        dp[s] = dp[s] || dp[s - val];
                    }
                }
            }
            if (!dp[tar]) return false;
        }
        return true;
    }

    public static int searchInTheRotateSortedWithDuplicates(int[] nums, int target) {

        int low = 0, high = nums.length - 1;

        while (low <= high) {

            int mid = low + (high - low) / 2;

            if (nums[mid] == target) {
                return mid;
            }

            if (nums[low] == nums[mid] && nums[mid] == nums[high]) {
                low ++;
                high --;
            } else if (nums[low] <= nums[mid]) {
                if (nums[low] <= target && target < nums[mid]) {
                    high = mid - 1;
                } else {
                    low = mid + 1;
                }
            } else {
                if (nums[mid] < target && target <= nums[high]) {
                    low = mid + 1;
                } else {
                    high = mid - 1;
                }
            }
        }
        return -1;
    }


    public static int min(int a, int b) {
        return a < b ? a : b;
    }
    public static int max(int a, int b) {
        return a > b ? a : b;
    }
}

