class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return countNums(r) - countNums(l - 1);
    }

private:
    using ll = long long;

    ll countNums(int n) {
        if (n <= 0) return 0;
        vector<int> d;
        while (n) d.push_back(n % 10), n /= 10;
        reverse(d.begin(), d.end());

        unordered_map<ll, ll> memo;
        return dfs(0, 1, 0, 0, 1, d, memo);
    }

    ll dfs(int i, int t, int s0, int s, int p, 
           const vector<int>& d, unordered_map<ll, ll>& memo) {
        if (i == d.size()) return s0 && s && (p % s == 0);
        ll key = i | (t << 6) | (s0 << 7) | (s << 8) | ((ll)p << 16);
        if (memo.count(key)) return memo[key];

        ll res = 0, lim = t ? d[i] : 9;
        for (int x = 0; x <= lim; x++) {
            res += dfs(i + 1, t && (x == lim), s0 || x, s + x * (s0 || x), 
                       s0 ? p * x : x, d, memo);
        }
        return memo[key] = res;
    }
};

