from functools import lru_cache

class Solution:
    def solveFunc(self):
        class FenwickTree:
            def __init__(self, n):
                self.farr = [0] * (n + 1)

            def prefixSum(self, idx):
                s = 0
                while idx > 0:
                    s += self.farr[idx]
                    idx -= (idx & -idx)
                return s

            def update(self, idx, val):
                while idx < len(self.farr):
                    self.farr[idx] += val
                    idx += (idx & -idx)

            def find(self, k):
                curr = 0
                ans = 0
                prevSum = 0
                for i in range(log2(k), -1, -1):
                    if self.farr[curr + (i << i)] + prevSum < k:
                        curr += (1 << i)
                        prevSum += self.farr[curr]
                return curr + 1


    def dx(self, ds):
        m = len(ds)

        @lru_cache(maxsize=None)
        def yf(i, t, s_flag, z_flag, s_val, p_val):
            if i == m:
                if not s_flag:
                    return 0
                return 1 if z_flag or (p_val % s_val == 0) else 0
            lim = ds[i] if t else 9
            tot = 0
            for d in range(lim + 1):
                nt = t and (d == lim)
                if not s_flag:
                    if d == 0:
                        tot += yf(i + 1, nt, False, False, 0, 1)
                    else:
                        tot += yf(i + 1, nt, True, False, d, d)
                else:
                    if d == 0:
                        tot += yf(i + 1, nt, True, True, s_val, 0)
                    else:
                        tot += yf(i + 1, nt, True, z_flag, s_val + d, (0 if z_flag else p_val * d))
            return tot
        return yf(0, True, False, False, 0, 1)

    def zFunction(self, patt, txt):
        st = patt + "#" + txt
        zArray = [0 for _ in range(len(st))]
        l = 0
        r = 0
        for i in range(1, len(st)):
            if r >= i:
                zArray[i] = min(r - i + 1, zArray[i - l])
            while i + zArray[i] < len(st) and st[zArray[i]] == st[zArray[i] + i]:
                zArray[i] += 1
            if zArray[i] + i - 1 > r:
                l = i
                r = i + zArray[i] - 1
        return zArray.count(len(patt))


    def fq(self, n):
        if n < 1:
            return 0
        return self.dx(self.oz(n))

    def beautifulNumbers(self, l, r):
        return self.fq(r) - self.fq(l - 1)

    def oz(self, n):
        return list(map(int, str(n)))

    def kmp(self, txt, patt):
        newString = patt + '#' + txt
        freqCount = 0
        freqArray = [0 for _ in range(len(newString))]
        i = 1
        length = 0
        while i < len(newString):
            if newString[i] == newString[length]:
                length += 1
                freqArray[i] = length
                i += 1
            else:
                if length > 0:
                    length = freqArray[length - 1]
                else:
                    freqArray[i] = 0
                    i += 1
        for i in freqArray:
            if i == len(patt):
                freqCount += 1
        return freqCount

    def countVisibleTowers(self, heights):
        n = len(heights)
        def nsr(arr):
            ans = []
            stack = []
            for i in range(len(arr) - 1, -1, -1):
                while stack and stack[-1][1] <= arr[i]:
                    stack.pop()
                if not stack:
                    ans.append(len(arr))
                else:
                    ans.append(stack[-1][0])
                stack.append([i, arr[i]])
            return ans[::-1]

        def nsl(arr):
            ans = []
            stack = []
            for i in range(len(arr)):
                while stack and stack[-1][1] <= arr[i]:
                    stack.pop()
                if not stack:
                    ans.append(-1)
                else:
                    ans.append(stack[-1][0])
                stack.append([i, arr[i]])
            return ans

        left = nsl(heights)
        right = nsr(heights)
        ans = []
        for i in range(n):
            val = i - left[i] - 1 + right[i] - i - 1
            ans.append(val)
        return ans

