class Solution:
    def count(self, x):
        digits = list(map(int, str(x)))
        n, dp = len(digits), {}
        def helper(num, lower, started, has_zero, s, p):
            key = (num, lower, started, has_zero, s, p)
            if key in dp:
                return dp[key]
            if num == n:
                if not started:
                    return 0
                dp[key] = 1 if (has_zero or (s and p % s == 0)) else 0
                return dp[key]
            ways = 0
            limit = digits[num] if lower else 9
            for d in range(limit + 1):
                new_lower = lower and (d == limit)
                if not started:
                    if d == 0:
                        ways += helper(num + 1, new_lower, False, False, 0, 1)
                    else:
                        ways += helper(num + 1, new_lower, True, False, d, d)
                else:
                    if d == 0 or has_zero:
                        ways += helper(num + 1, new_lower, True, True, 0, 1)
                    else:
                        ways += helper(num + 1, new_lower, True, False, s + d, p * d)
            dp[key] = ways
            return ways

        return helper(0, True, False, False, 0, 1)   

    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.count(r) - self.count(l-1)

