                res = 0
                limit = digits[pos] if tight else 9
                for d in range(limit + 1):
                    ntight = tight and (d == limit)
                    if not started:
                        if d == 0:
                            res += dp(pos + 1, ntight, False, False, 0, 1)
                        else:
                            res += dp(pos + 1, ntight, True, (d == 0), d, d)
                    else:
                        n_has_zero = has_zero or (d == 0)
                        n_s = s + d
                        n_p = p * d if d != 0 else 0
                        res += dp(pos + 1, ntight, True, n_has_zero, n_s, n_p)
                return res
            return dp(0, True, False, False, 0, 1)
        return count_up_to(r) - count_up_to(l - 1)

