class Solution {

    public int beautifulNumbers(int l, int r) {
        return beautifulNumbers(r + "") - beautifulNumbers(l - 1 + "");
    }

    private int beautifulNumbers(String s) {
        HashMap<Integer, Integer>[][] map = new HashMap[9][72];
        for (int i = 0; i < map.length; i++) {
            for (int j = 0; j < map[0].length; j++) {
                map[i][j] = new HashMap<>();
            }
        }
        return beautifulNumbers(s, 0, false, 0, 0, map);
    }

    private int beautifulNumbers(String s, int i, boolean flag, int prod, int sum, HashMap<Integer, Integer>[][] map) {
        if (i == s.length()) {
            return sum != 0 && prod % sum == 0 ? 1 : 0;
        } else if (flag) {
            int result = 0;
            if (sum == 0) {
                result += beautifulNumbers(s, i + 1, true, 0, 0, map);
                for (int j = 1; j < 10; j++) {
                    result += beautifulNumbers(s.length() - i - 1, j, j, map);
                }
            } else {
                result += beautifulNumbers(s.length() - i, prod, sum, map);
            }
            return result;
        }
        int result = beautifulNumbers(s, i + 1, false, (s.charAt(i) - '0') * (i > 0 ? prod : 1), sum + s.charAt(i) - '0', map);
        for (int j = 0; j < s.charAt(i) - '0'; j++) {
            result += beautifulNumbers(s, i + 1, true, i > 0 ? j * prod : j, sum + j, map);
        }
        return result;
    }

    private int beautifulNumbers(int i, int prod, int sum, HashMap<Integer, Integer>[][] map) {
        if (i == 0) {
            return prod % sum == 0 ? 1 : 0;
        } else if (map[i][sum].containsKey(prod)) {
            return map[i][sum].get(prod);
        }
        int result = 0;
        for (int j = 0; j < 10; j++) {
            result += beautifulNumbers(i - 1, prod * j, sum + j, map);
        }
        map[i][sum].put(prod, result);
        return result;
    }
}

