class Solution {
    fun beautifulNumbers(l: Int, r: Int): Int {
        val dp = intArrayOf(l, r)
        fun cnt(n: Int): Long {
            val s = n.toString()
            data class S(val i: Int, val t: Boolean, val st: Boolean, val su: Int, val pr: Int)
            val memo = HashMap<S, Long>()
            fun f(i: Int, t: Boolean, st: Boolean, su: Int, pr: Int): Long {
                if (i == s.length) return if (st && su > 0 && pr % su == 0) 1L else 0L
                val state = S(i, t, st, su, pr)
                if (memo.containsKey(state)) return memo[state]!!
                var res = 0L
                val lim = if (t) s[i] - '0' else 9
                for (d in 0..lim) {
                    val nt = t && (d == lim)
                    res += if (!st && d == 0) f(i + 1, nt, false, 0, 1) else f(i + 1, nt, true, su + d, pr * d)
                }
                memo[state] = res
                return res
            }
            return f(0, true, false, 0, 1)
        }
        return (cnt(dp[1]) - if (dp[0] > 1) cnt(dp[0] - 1) else 0L).toInt()
    }
}

