class Solution {
    using ll = long long;
    unordered_map<string, ll> memo;

    ll dp(const vector<int>& digs, int pos, int tight, int started, int sum, int prod) {
        if (pos == digs.size())
            return started && sum > 0 && prod % sum == 0 ? 1LL : 0LL;
        string key = to_string(pos) + "_" + to_string(tight) + "_" + to_string(started) + "_" + to_string(sum) + "_" + to_string(prod);
        if (memo.count(key)) return memo[key];
        ll ans = 0;
        int limit = tight ? digs[pos] : 9;
        for (int d = 0; d <= limit; d++) {
            int ntight = (tight && d == limit) ? 1 : 0;
            if (!started) {
                if (d == 0)
                    ans += dp(digs, pos + 1, ntight, 0, 0, 1);
                else
                    ans += dp(digs, pos + 1, ntight, 1, d, d);
            } else {
                int nsum = sum + d;
                int nprod = (prod == 0 || d == 0) ? 0 : prod * d;
                ans += dp(digs, pos + 1, ntight, 1, nsum, nprod);
            }
        }
        return memo[key] = ans;
    }

    ll countBeautiful(ll x) {
        if (x < 1) return 0;
        string s = to_string(x);
        vector<int> digs(s.size());
        for (int i = 0; i < s.size(); i++)
            digs[i] = s[i] - '0';
        memo.clear();
        return dp(digs, 0, 1, 0, 0, 1);
    }

public:
    int beautifulNumbers(int l, int r) {
        return (int)(countBeautiful(r) - countBeautiful(l - 1));
    }
};

